func ShowImage(m image.Image) {
	var buf bytes.Buffer
	err := png.Encode(&buf, m)
	if err != nil {
		panic(err)
	}
	enc := base64.StdEncoding.EncodeToString(buf.Bytes())
	fmt.Println("IMAGE:" + enc)
}

func main() {
	header := bytes.NewBuffer(nil)
	r := io.TeeReader(body, header)

	_, name, err := image.DecodeConfig(r)
	if err != nil {
		fmt.Println(err)
		return
	}
	log.Println(name) // jpeg

	img, err := jpeg.Decode(io.MultiReader(header, body))
	if err != nil {
		fmt.Println(err) // unexpected EOF
		return
	}
	ShowImage(img)
}
